Scripts.OnLoad:
self:SetStatusBarTexture(self.texture) self.texture:SetVertexColor(1, .5, .2, 1)
function self:Set(value, max, nAndMaxN) if not self:IsShown() then self.FadeIn:Play() end self:SetMinMaxValues(0, max) self:SetValue(value)
if self:IsShown() then if nAndMaxN[2] > 1 then self.text:SetFormattedText("%.0f%% (%d/%d)", value/max*100, nAndMaxN[1], nAndMaxN[2]) else self.text:SetFormattedText("%.0f%%", value/max*100) end end
if value >= max and nAndMaxN[1] >= nAndMaxN[2] then self.FadeOut:Play() end end self:SetFrameLevel(self:GetParent():GetFrameLevel()) self.realParent = self:GetParent() --self:SetParent(TellMeWhen_IconEditor)

Scripts.OnTextChanged:
local text = self:GetText()
if userInput then TMW.DD:CloseDropDownMenus()
local editboxResults = TMW.LastExportedString ~= text and TMW:DeserializeData(text, true) if editboxResults then self.DoPulseValidString = 1 else self.DoPulseValidString = nil end end

Scripts.OnLoad:
self:SetBackdrop({
	edgeFile = "Interface\\Tooltips\\UI-Tooltip-Background",
	tile = true,
	edgeSize = 3,
})
self:SetBackdropBorderColor(0, .82, 1, 1)

						TMW:CInit(self)
						self:SetTexts("Freeze", "Stop updating the text so you can examine it or copy it to your clipboard.")

						self:SetSetting("Frozen")
						self:CScriptAdd("SettingTableRequested", function(requester)
							return self:GetParent()
						end)
						self:CScriptAdd("ReloadRequested", function()
							self:SetEnabled(TMW.profilingEnabled)
						end)

						TMW:CInit(self)
						self:SetWidth(200)

						self:SetTexts("Select Columns")

						self:SetEasyFunctions(
							function()
								return pairs(TMW.IE.CpuReportParameters.Columns)
							end,
							function(info, i, column)
								info.text = column.title
								info.tooltipTitle = column.title
								info.tooltipText = column.desc
								info.value = column
								info.checked = column.selected
								info.keepShownOnClick = true
								info.isNotRadio = true
							end,
							function(button, dropdown)
								local column = button.value
								column.selected = not column.selected
								self:OnSettingSaved()
							end
						)

						self:SetScrollable(true)
